import { NavLink } from 'react-router-dom';
import classes from './Dialogs.module.css'

const DialogItem = (props) => {
    let path = "/dialogs/" + props.id;
    return <div className={classes.dialog}>
        <NavLink to={path} activeClassName={classes.active}>{props.name}</NavLink>
    </div>
}

const Message = (props) => {
    return <div className={classes['message']}>{props.message}</div>
}

const Dialogs = () => {
    return (
        <div className={classes['dialogs']}>
            <div className={classes['dialogs-items']}>
                <DialogItem name="Паша" id="1" />
                <DialogItem name="Катя" id="2" />
                <DialogItem name="Эльдор" id="3" />
            </div>
            <div className={classes['dialogs-messages']}>
                <Message message="Привет!" />
                <div className={classes['message']}>Как дела?</div>
                <div className={classes['message']}>Что делаешь?</div>
            </div>
        </div>
    )
};

export default Dialogs;
